import React, { useRef, useEffect, useState } from 'react';
import { Navbar } from '../../components';

const Biology = () => {
  // Create a ref to the Navbar component
  const navbarRef = useRef(null);

  // Create a state variable to store the Navbar's height
  const [navbarHeight, setNavbarHeight] = useState(0);

  // Use useEffect to measure the Navbar's height when the component mounts
  useEffect(() => {
    if (navbarRef.current) {
      const height = navbarRef.current.offsetHeight;
      setNavbarHeight(height);
    }
  }, []);

  // Calculate the available height for the content
  const contentHeight = `calc(100vh - ${navbarHeight}px)`;

  return (
    <div>
      <Navbar ref={navbarRef} />
      <div className="bg-white" style={{ height: contentHeight }}>
        <p>hi</p>
      </div>
    </div>
  );
};

export default Biology;
