import React from 'react'
import 'tailwindcss/tailwind.css';
import 'font-awesome/css/font-awesome.min.css';

const Hero = () => {
  return (
    <div className="text-white">
      <div className="w-full h-screen max-w-[800px] mx-auto
                      text-center flex flex-col justify-center">
        <p className="text-transparent bg-gradient-to-r from-teal-400 via-blue-400 to-blue-600 font-bold p-2 bg-clip-text">EMBRACE LEARNING WITH US</p>
        <h1 className="md:text-7xl sm:text-6xl text-4xl font-bold md:py-6">Learn with EduBahamas</h1>
        <div>
          <p className="md:text-3xl sm:text-2xl text-xl font-bold">Study, Engage, Test for</p>
        </div>
      </div>
    </div>
  )
}

export default Hero
