import React from 'react'
import Outline from './Outline';
const Content = ({unit_num, unit_title}) => {

    const ecosystemModule = [
        {
            name: "Mangrove Ecosysystems",
            link: "/mangrove",
        },
        {
            name: "Coral Reef Ecosysystems",
            link: "/coral-reef",
        },
        {
            name: "Rocky Shores",
            link: "/rocky-shores",
        },
        {
            name: "Sandy Beaches",
            link: "/sandy-beaches",
        },
    ];

    const pollutionModule = [
        {
            name: "Air Pollution",
            link: "/air-pollution",
        },
        {
            name: "Land Pollution",
            link: "/land-pollution",
        },
        {
            name: "Water Pollution",
            link: "/water-pollution",
        },
        {
            name: "Ozone Depletion & Global Climate Change",
            link: "/climate-change",
        },
    ];

    const biodiversityModule = [
        {
            name: "Biodiversity",
            link: "/biodiversity",
        },
        {
            name: "National Parks & Protected Areas",
            link: "/protected-areas",
        },
        {
            name: "Fisheries Management",
            link: "/fisheries-management",
        },
    ];

  return (
    <div className="h-screen">
        <h1 className="text-3xl font-bold">Unit {unit_num}: {unit_title}</h1>
        <div className="flex flex-col py-5">
            <div className="w-[90%] bg-white px-5 py-5 rounded-md">
                <h1 className="text-xl">About This Unit</h1>
                <p className="text-sm pt-2">
                    Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum.
                    Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum. Lorem Ipsum.
                </p>
            </div>
            <div className="mt-[2%] w-[90%] bg-white px-5 py-5 rounded-md">
                <Outline title="Ecosystems" items={ecosystemModule} />
            </div>
            <div className="mt-[2%] w-[90%] bg-white px-5 py-5 rounded-md">
                <Outline title="Pollution" items={pollutionModule} />
            </div>
            <div className="mt-[2%] w-[90%] bg-white px-5 py-5 rounded-md">
                <Outline title="Biodiversity" items={biodiversityModule} />
                {/* <h1 className="text-lg">Biodiversity</h1>
                <p className="text-sm pt-2 text-gray-500">
                    LEARN
                    <ul className="flex flex-col text-black">
                        <li className="py-2"><a className="bg-transparent before:hover:w-0 hover:bg-transparent hover:underline" href="">Biodiversity</a></li>
                        <li className="py-2"><a className="bg-transparent before:hover:w-0 hover:bg-transparent hover:underline" href="">National Parks & Protected Areas</a></li>
                        <li className="py-2"><a className="bg-transparent before:hover:w-0 hover:bg-transparent hover:underline" href="">Fisheries Management</a></li>              
                    </ul>
                </p> */}
            </div>
        </div>
    </div>
  )
}

export default Content
