import React, {useState} from "react";
import './register.css';
import img from '../assets/reg_user.svg'
const Register = () => {

    const [selectedOption, setSelectedOption] = useState('');

  // Function to handle the option change
  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

    return (
        <div className="grid md:grid-cols-2 sm:grid-cols-1">
            <div>
                <img src={img} className="w-[700px]"/>
            </div>
            <div className="">
                <form className="border border-white mx-20 rounded-md">
                    <h2 className="py-3 mb-[50px] text-white display-flex justify-center text-center text-3xl font-bold">
                        Register
                    </h2>

                    <div className="text-left display-block">
                        <label className="text-left order-2 w-[30%] py-0 my-0 text-lg">Email</label>
                        <input className="py-5 my-0" type="text" placeholder="Enter email" />
                    </div>

                    <div className="my-5">
                        <label className="py-0 my-0 w-[35%] text-lg">Password</label>
                        <input className="py-5 my-0" type="password" placeholder="Enter password" />
                    </div>

                    <div className="my-5">
                        <label className="py-0 my-0 w-[48%] text-lg"> Re-Enter Password</label>
                        <input className="py-5 my-0" type="password" placeholder="Enter password" />
                    </div>

                    <div className="my-5">
                        <label className="py-0 my-0 w-[40%] text-lg"> Select School </label>
                        <select className="py-2 rounded-md flex justify-content w-[50%] ml-[110px] px-2" id="country" value={selectedOption} onChange={handleOptionChange}>
                            <option className="option-text-grey" value="">Select a school</option>
                            <option value="usa">Queen's College</option>
                            <option value="canada">Kingsway Academy</option>
                            <option value="uk">St. Augustine's College</option>
                            <option value="uk">Individual</option>
                        </select>
                    </div>

                    <div className="my-5">
                        <label className="py-0 my-0 w-[40%] text-lg"> Select School </label>
                        <select className="py-2 rounded-md flex justify-content w-[50%] ml-[110px] px-2" id="country" value={selectedOption} onChange={handleOptionChange}>
                            <option className="option-text-grey" value="">Select a school</option>
                            <option value="usa">Student</option>
                            <option value="canada">Teacher</option>
                        </select>
                    </div>

                    <button className="bg-[#1896b9] hover:bg-[#1896b980]"> Register Now </button>
                </form>
            </div>
        </div>
        // <div className=" w-full-[1240px] py-16 text-white px-4 bg-yellow-600">
        //     <div className="max-w-screen-xl mx-auto grid lg:grid-cols-3 bg-white py-10 ">
        //         <div className="lg:col-span-2 py-2 px-4 bg-blue-600">
        //             <h1>PICTURE</h1>
        //         </div>
        //         <div className="bg-red-600 py-2 px-4">
        //             <h1> LOGIN FORM </h1>
        //         </div>
        //     </div>
        // </div>
    );
};

export default Register;
